mapper.select:
select id,username,email from user t


		and t.username = #{username}


		and t.password = #{password}


		and t.email = #{email}

mapper.update:
update user
set coin=coin+#{coin}
where id=#{id} and coin+#{coin} >=0

update user
set signdate=CURRENT_TIMESTAMP

		,coin=coin+#{coin}


		,signin=signin+#{signin}


where id=#{id} 

	and coin+#{coin}>=0


	and signin+#{signin}>=0
